    private void loadData() {
        String[] judulkolom = {"Akun_Id","Username","Jabatan"};
        String kolom = "login.Akun_Id,login.Username,login.Jabatan";
        tblDaftarAkun.setModel(db.tampilData("login", kolom, judulkolom));
        
    }
    
    private void gantipw(boolean y){
        txtPwLama.setVisible(y);
        lblGantiPw.setVisible(y);
        cbTampilkan2.setVisible(y);
        if(y){
            lblPw.setText("Password Baru");
        } else {
            lblPw.setText("Password");
        }
    }
    
    private void aktifkan(boolean x) {
        txtIdAkun.setEditable(x);
        txtUsername.setEditable(x);
        txtPwLama.setEditable(x);
        txtPwKonfir.setEditable(x);
        listJabatan.setEditable(x);
        
        btnTambah.setEnabled(!x);
        btnEdit.setEnabled(!x);
        btnSimpan.setEnabled(x);
        btnBatal.setEnabled(x);
        btnHapus.setEnabled(!x);
        
        
        tblDaftarAkun.setEnabled(!x);
    }
    
    private void kosongkan() {
        txtIdAkun.setText("");
        txtUsername.setText("");
        txtPwLama.setText("");
        txtPwKonfir.setText("");
        listJabatan.setSelectedIndex(-1);
        txtPassword.setText("");
    }

