    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        // TODO add your handling code here:
        gantipw(true);
        int baris = tblDaftarAkun.getSelectedRow();
        
        if(baris > -1) {
            aktifkan(true);
            databaru = false;
            txtIdAkun.setEditable(false);
            txtUsername.requestFocus();
            
            txtIdAkun.setText(tblDaftarAkun.getValueAt(baris, 0).toString());
            txtUsername.setText(tblDaftarAkun.getValueAt(baris, 1).toString());
            jabatan = tblDaftarAkun.getValueAt(baris, 2).toString();
            if(jabatan.equals("Admin")){
                listJabatan.setSelectedIndex(0);
            }else if(jabatan.equals("Pemilik")){
                listJabatan.setSelectedIndex(1);
            }else{
                listJabatan.setSelectedIndex(3);
            }
            
        }else{
            JOptionPane.showMessageDialog(this, "Pilih Data untuk edit");
        }
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        // TODO add your handling code here:
        if(txtIdAkun.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "ID Akun wajib diisi.");
            txtIdAkun.requestFocus();
        } else if(txtPassword.getText().equals(txtPwKonfir.getText())) {
            if(txtUsername.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Nama Produk wajib diisi.");
                txtUsername.requestFocus();
            } else if(listJabatan.getSelectedIndex() == -1) {
             JOptionPane.showMessageDialog(this, "Jabatan Wajib Dipilih.");
              listJabatan.requestFocus();
            } else {
                if(databaru) {
                    String value = "'" + txtIdAkun.getText() + 
                                   "','" + txtUsername.getText() + 
                                   "','" + txtPassword.getText() + 
                                   "','" + listJabatan.getSelectedItem().toString()+ "'";
                    hasil = db.tambahData("login", value);
                    kosongkan();
                    loadData();
                } else {
                    if(txtPwLama.getText().isEmpty()){
                        JOptionPane.showMessageDialog(this, "Password lama wajib diisi.");
                        txtPwLama.requestFocus();
                    } else {
                        ResultSet res = db.getData("SELECT * FROM login WHERE Akun_id = '" + txtIdAkun.getText() + "'");
                        try {
                            if(res.next()) {
                                String passinput = txtPwLama.getText();
                                String passcek = res.getString("password");
                                if(passinput.equals(passcek)){
                                    String value = "Username='" + txtUsername.getText() +
                                            "', password='" + txtPassword.getText() +
                                            "', Jabatan='" + listJabatan.getSelectedItem().toString()+ "'";
                                    hasil = db.editData("login", value, "Akun_id='" + txtIdAkun.getText() + "'");
                                } else {
                                    JOptionPane.showMessageDialog(this, "Password Lama Salah.");
                                    txtPwLama.requestFocus();
                                }
                            }  
                        } catch (SQLException ex) {
                            Logger.getLogger(Daftar_Akun.class.getName()).log(Level.SEVERE, null, ex);
                            }
                        
                    }
                    if(hasil > 0) {
                        JOptionPane.showMessageDialog(this, "Data berhasil ditambah/edit.");
                        aktifkan(false);
                        kosongkan();
                        loadData();
                    } else {
                    JOptionPane.showMessageDialog(this, "Data gagal ditambah/edit.");
                    }
                }
            } 
        } else {
            JOptionPane.showMessageDialog(this, "Password tidak sama");
            txtPwKonfir.requestFocus();
        }
    }//GEN-LAST:event_btnSimpanActionPerformed

