    private void btnLPembelianActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLPembelianActionPerformed
        // TODO add your handling code here:
        try {
            String ResPeriode = JOptionPane.showInputDialog(null, "Masukkan periode \n Contoh : 03/2023", 
                    "laporan Per-Bulan", JOptionPane.QUESTION_MESSAGE);
            Connection conn = DriverManager.getConnection(url, user, pass);
            String file ="..\\dist\\src\\majujayafuniture\\LaporanPembelian.jrxml";
            HashMap parameter = new HashMap();
            parameter.put ("pPeriode", ResPeriode);;
            JasperReport jr;
            jr = JasperCompileManager.compileReport(file);
            JasperPrint jp = JasperFillManager.fillReport(jr,parameter,conn);
            JasperViewer.viewReport(jp,false);
            
        } catch (JRException | SQLException ex) {
            Logger.getLogger(Menu.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnLPembelianActionPerformed

