    private void transaksiBaru() {
        txtNoTrans.setText(db.generateNomor("NoNota","Jual","TglJual"));
        txtTglTrans.setText(new SimpleDateFormat("dd-MMMM-yyyy").format(Calendar.getInstance().getTime()));
        String TglJual = new SimpleDateFormat("yy-MM-dd").format(Calendar.getInstance().getTime());
        loadData();
        
        int hasil;
        String value = "'" + txtNoTrans.getText() + "','" + TglJual + "'";
                    hasil = db.tambahData("jual(NoNota,TglJual)", value);
        if(hasil > 0) {
            JOptionPane.showMessageDialog(this, "Data berhasil ditambah/edit.");
        } else {
            JOptionPane.showMessageDialog(this, "Data gagal ditambah/edit.");
        }
    }
    
    private void loadData() {
        String[] judulkolom = {"Kode","Nama","Harga Jual","Jumlah","Sub Total"};
        String kolom = "produk.KodeBrg,produk.NamaBrg,jual_detail.HargaJual,jual_detail.Qty,(jual_detail.HargaJual*jual_detail.Qty) as subtotal";
        tblDaftarPrd.setModel(db.tampilData("jual_detail LEFT JOIN produk ON jual_detail.KodeBrg=produk.KodeBrg WHERE NoNota='" + 
                txtNoTrans.getText() + "'", kolom, judulkolom));
        
        int total = 0;
        for(int i=0; i<tblDaftarPrd.getRowCount(); i++) {
            total += Integer.valueOf(tblDaftarPrd.getValueAt(i, 5).toString());
        }
        txtTotal.setText(Integer.toString(total));
        
        
    }
    
    private void isilist(){
        listBrg.setModel(db.isiList("produk", "KodeBrg"));
    }
    
