    private void btnOKActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnOKActionPerformed
        // TODO add your handling code here:
        if(listBrg.isSelectionEmpty()) {
            JOptionPane.showMessageDialog(this, "Kode Produk wajib diisi.");
            listBrg.requestFocus();
        } else if (Integer.valueOf(spnQTY.getValue().toString()) <= 0) {
            JOptionPane.showMessageDialog(this, "Jumlah wajib diisi.");
            spnQTY.requestFocus();
        } else if(txtHgJual.getText().isEmpty()){
            JOptionPane.showMessageDialog(this, "Harga Jual wajib diisi.");
            txtHgJual.requestFocus();
        } else {
            try{
                ResultSet res = db.getData("SELECT * FROM jual_detail WHERE KodeBrg = '" +
                    listBrg.getSelectedValue() + "' AND NoNota = '" +
                    txtNoTrans.getText() + "'");
                int hasil;
                if(res.next()) {
                    JOptionPane.showMessageDialog(this, "Barang telah di imput, Harap hapus terlebih dahulu.");
                    hasil = 0;
                } else {

                    String value = "'" + txtNoTrans.getText() +
                        "','" + listBrg.getSelectedValue() +
                        "','" + txtHgJual.getText() +
                        "','" + spnQTY.getValue().toString() +
                        "','" + txtSubtotal.getText() + "'";
                    hasil = db.tambahData("jual_detail(NoNota,KodeBrg,HargaJual,Qty,Subtotal)", value);
                }

                if(hasil > 0) {
                    int total = Integer.parseInt(txtTotal.getText());
                    total = total + Integer.parseInt(txtSubtotal.getText());
                    txtTotal.setText(String.valueOf(total));
                    
                    listBrg.setSelectedIndex(-1);
                    txtNamaPrd.setText("");
                    spnQTY.setValue(0);
                    txtHgJual.setText("");
                    txtSubtotal.setText("");
                    loadData();
                } else {
                    JOptionPane.showMessageDialog(this, "Produk gagal ditambah/edit.");
                }
            } catch(SQLException ex) {
                System.err.println(ex.getMessage());
            }
        }
    }//GEN-LAST:event_btnOKActionPerformed

