    private void btnCariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCariActionPerformed
        // TODO add your handling code here:
        if(!listBrg.isSelectionEmpty()) {
            try{
                ResultSet res = db.getData("SELECT * FROM produk WHERE KodeBrg = '" + listBrg.getSelectedValue() + "'");
                if(res.next()) {
                    txtNamaPrd.setText(res.getString("NamaBrg"));
                    txtHgJual.requestFocus();
                    
                } else {
                    JOptionPane.showMessageDialog(this, "Produk tidak ditemukan.");
                    listBrg.requestFocus();
                }
            } catch(SQLException ex) {
                System.err.println(ex.getMessage());
            }
        } else {
            JOptionPane.showMessageDialog(this, "Kode Produk wajib dipilih.");
            listBrg.requestFocus();
        }
    }//GEN-LAST:event_btnCariActionPerformed

    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        // TODO add your handling code here:
        String value = null;
        if (tblDaftarPrd.getRowCount() <= 0) {
            JOptionPane.showMessageDialog(this, "Transaksi masih kosong.");
        } else if (txtNoTelp.getText().isEmpty()){ 
            JOptionPane.showMessageDialog(this, "Nomor Telp Pelanggan masih kosong.");
            txtNoTelp.requestFocus();
        } else if (txtNmPelanggan.getText().isEmpty()){ 
            JOptionPane.showMessageDialog(this, "Nama Pelanggan masih kosong.");
            txtNmPelanggan.requestFocus();
        } else if (txtAlamat.getText().isEmpty()){ 
            JOptionPane.showMessageDialog(this, "Alamat Pelanggan masih kosong.");
            txtAlamat.requestFocus();
        } else if(txtBayar.getText().equals("0")){
            JOptionPane.showMessageDialog(this, "Nominal Bayar masih kosong.");
            txtBayar.requestFocus();
        } else {
            if(Integer.parseInt(txtSisa.getText())<0){
                int sisa = Integer.parseInt(txtSisa.getText());
                int panjar = sisa*-1;
                value = "Telp ='" + txtNoTelp.getText() +
                        "',NamaPlg ='" + txtNmPelanggan.getText() +
                        "',Alamat ='" + txtAlamat.getText() +
                        "',TotalHarga ="+txtTotal.getText()+
                        ",status='Belum Lunas'" +
                        ",Dp = "+txtBayar.getText() +
                        ",Sisa ="+panjar;
            } else {
                value = "Telp ='" + txtNoTelp.getText() +
                        "',NamaPlg ='" + txtNmPelanggan.getText() +
                        "',Alamat ='" + txtAlamat.getText() + 
                        "',TotalHarga ="+txtTotal.getText()+
                        ",status='Sudah Lunas'";
            }
            
            
            if(db.editData("jual", value, "NoNota = '"+txtNoTrans.getText()+"'")>0) {
                int jumlah = tblDaftarPrd.getRowCount();
                int hasil = 0;
                for(int i=0; i<jumlah; i++) {
                    String stok = "stok=stok-" + tblDaftarPrd.getValueAt(i, 3).toString();
                    String kode = tblDaftarPrd.getValueAt(i, 0).toString();
                    hasil += db.editData("produk", stok, "KodeBrg='" + kode + "'");
                }
                
                if(hasil == jumlah) {
                    JOptionPane.showMessageDialog(this, "Transaksi berhasil disimpan.");
                    int tanya = JOptionPane.showConfirmDialog(this, "Cetak Faktur ?", 
                    "Hapus Data", JOptionPane.YES_NO_OPTION, 
                    JOptionPane.QUESTION_MESSAGE);
            
                if(tanya == JOptionPane.YES_OPTION) {
                    try {
                        Connection conn = DriverManager.getConnection(url, user, pass);
                        String file ="..\\dist\\src\\majujayafuniture\\Faktur_Jual.jrxml";
                        HashMap parameter = new HashMap ();
                        parameter.put ("pNoNota", txtNoTrans.getText());
                        JasperReport jr;
                        jr = JasperCompileManager.compileReport(file);
                        JasperPrint jp = JasperFillManager.fillReport(jr,parameter,conn);
                        JasperViewer.viewReport(jp,false);
            
                    } catch (JRException | SQLException ex) {
                        Logger.getLogger(Menu.class.getName()).log(Level.SEVERE, null, ex);
                    }
                }
                    kosongkan();
                    transaksiBaru();
                }
            }
        }
    }//GEN-LAST:event_btnSimpanActionPerformed

