    private void loadData() {
        String[] kolom = {"Kode", "Nama", "Modal", "Stok"};
        tblProduk.setModel(db.tampilData("produk", kolom));
    }
    
    private void aktifkan(boolean x) {
        txtKode.setEditable(x);
        txtNama.setEditable(x);
        txtModal.setEditable(x);
        txtStok.setEditable(x);
        
        btnTambah.setEnabled(!x);
        btnEdit.setEnabled(!x);
        btnSimpan.setEnabled(x);
        btnBatal.setEnabled(x);
        btnHapus.setEnabled(!x);
        btnKeluar.setEnabled(!x);
        
        tblProduk.setEnabled(!x);
    }
    
    private void kosongkan() {
        txtKode.setText("");
        txtNama.setText("");
        txtModal.setText("");
        txtStok.setText("");
    }
