    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown
        // TODO add your handling code here:
        loadData();
        aktifkan(false);
    }//GEN-LAST:event_formComponentShown

    private void btnTambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTambahActionPerformed
        // TODO add your handling code here:
        aktifkan(true);
        databaru = true;
        kosongkan();
        txtKode.requestFocus();
    }//GEN-LAST:event_btnTambahActionPerformed

    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        // TODO add your handling code here:
        int baris = tblProduk.getSelectedRow();
        
        if(baris > -1) {
            aktifkan(true);
            databaru = false;
            txtKode.setEditable(false);
            txtNama.requestFocus();
            
            txtKode.setText(tblProduk.getValueAt(baris, 0).toString());
            txtNama.setText(tblProduk.getValueAt(baris, 1).toString());
            txtModal.setText(tblProduk.getValueAt(baris, 2).toString());
            txtStok.setText(tblProduk.getValueAt(baris, 3).toString());
        }else{
            JOptionPane.showMessageDialog(this, "Pilih Data untuk edit");
        }
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSimpanActionPerformed
        // TODO add your handling code here:
        if(txtKode.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Kode Produk wajib diisi.");
            txtKode.requestFocus();
//        (buat validasi jumlah char = 10)} 
        } else if(txtKode.getText().length()< 10) {
            JOptionPane.showMessageDialog(this, "Kode Produk Kurang Dari 10 Karakter.");
            txtKode.requestFocus();
        } else if(txtKode.getText().length()> 10) {
            JOptionPane.showMessageDialog(this, "Kode Produk Lebih Dari 10 Karakter.");
            txtKode.requestFocus();
        } else if(txtNama.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Nama Produk wajib diisi.");
            txtNama.requestFocus();
        } else if(txtNama.getText().length()> 25) {
            JOptionPane.showMessageDialog(this, "Nama Produk Lebih Dari 25 Karakter.");
            txtNama.requestFocus();
        } else if(txtModal.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Modal Produk wajib diisi.");
            txtModal.requestFocus();
        } else if(txtStok.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Stok Produk wajib diisi.");
            txtStok.requestFocus();
        } else {
            int hasil;
            
            if(databaru) {
                String value = "'" + txtKode.getText() + 
                               "','" + txtNama.getText() + 
                               "','" + txtModal.getText() + 
                               "','" + txtStok.getText() + "'";
                hasil = db.tambahData("produk", value);
            } else {
                String value = "NamaBrg='" + txtNama.getText() + 
                            "', HargaBeli='" + txtModal.getText() + 
                            "', Stok='" + txtStok.getText() + "'";
                hasil = db.editData("produk", value, "KodeBrg='" + txtKode.getText() + "'");
            }
            
            if(hasil > 0) {
                JOptionPane.showMessageDialog(this, "Data berhasil ditambah/edit.");
                aktifkan(false);
                kosongkan();
                loadData();
            } else {
                JOptionPane.showMessageDialog(this, "Data gagal ditambah/edit.");
            }
        }
    }//GEN-LAST:event_btnSimpanActionPerformed

    private void btnBatalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBatalActionPerformed
        // TODO add your handling code here:
        aktifkan(false);
        kosongkan();
    }//GEN-LAST:event_btnBatalActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
        // TODO add your handling code here:
        int baris = tblProduk.getSelectedRow();
        
        if(baris > -1) {
            int tanya = JOptionPane.showConfirmDialog(this, "Yakin Hapus ?", 
                    "Hapus Data", JOptionPane.YES_NO_OPTION, 
                    JOptionPane.QUESTION_MESSAGE);
            
            if(tanya == JOptionPane.YES_OPTION) {
                int hasil = db.hapusData("produk", "Kodebrg = '" + 
                        tblProduk.getValueAt(baris, 0).toString() + "'");
                
                if(hasil > 0) {
                    JOptionPane.showMessageDialog(this, "Data berhasil dihapus.");
                    loadData();
                } else {
                    JOptionPane.showMessageDialog(this, "Data gagal dihapus.");
                }
            }
        }else{
            JOptionPane.showMessageDialog(this, "Pilih Data untuk hapus");
        }
    }//GEN-LAST:event_btnHapusActionPerformed

